const result = {};

// Process each item
for (const item of $input.all()) {
  // Get the key-value pair from each item
  const [key, value] = Object.entries(item.json)[0];

  // Initialize array if this intelligence type hasn't been seen
  if (!result[key]) {
    result[key] = [];
  }

  // Add the value to the array
  result[key].push(value);
}

return [{ json: result }];
