// N8N Code Node
// Input: $input.first().json.answers (array of learning styles)

const answers = $input.first().json.answers;

// Count occurrences of each learning style
const counts = {
  "Aural": 0,
  "Visual": 0,
  "Read/Write": 0,
  "Kinesthetic": 0
};

answers.forEach(answer => {
  counts[answer]++;
});

// Return the counts as output
return [{
  json: counts
}];
